              <Transaction />
            </List>
          </Grid.Column>
        </Grid>
      </Segment>
    </div>
  );
}

function mapStateToProps(state) {
  return {
    user_id: state.userId,
    balance: state.balance
  };
}

export default connect(mapStateToProps, null)(TransactionsContainer);
